import React, { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

function App() {
  const [address, setAddress] = useState('');
  const [walletData, setWalletData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!address.trim()) {
      setError('Please enter a wallet address');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await axios.post('http://localhost:3001/api/analyze-wallet', { address });

      // Ensure recommendations is an array
      if (response.data && response.data.persona && !Array.isArray(response.data.persona.recommendations)) {
        response.data.persona.recommendations = [];
      }

      // Ensure tags is an array
      if (response.data && response.data.persona && !Array.isArray(response.data.persona.tags)) {
        response.data.persona.tags = [];
      }

      setWalletData(response.data);
    } catch (err) {
      console.error('Error analyzing wallet:', err);
      setError('Failed to analyze wallet. Please try again.');
      setWalletData(null);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container py-5">
      <h1 className="text-center mb-4">Wallet Persona Analyzer</h1>
      <div className="card p-4 mb-4">
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label className="form-label">Enter Ethereum Wallet Address</label>
            <input
              type="text"
              className="form-control"
              placeholder="0x..."
              value={address}
              onChange={(e) => setAddress(e.target.value)}
            />
            <div className="form-text text-muted">
              Enter a valid Ethereum wallet address to analyze its on-chain persona.
            </div>
          </div>
          <button 
            type="submit" 
            className="btn btn-primary w-100"
            disabled={loading}
          >
            {loading ? (
              <>
                <span className="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span>
                <span className="ms-2">Analyzing...</span>
              </>
            ) : (
              'Analyze Wallet'
            )}
          </button>
        </form>
      </div>

      {error && (
        <div className="alert alert-danger mt-3">
          {error}
        </div>
      )}

      {walletData && walletData.persona && (
        <div className="card mt-4">
          <div className="card-header bg-primary text-white">
            <h2>Wallet Profile</h2>
            <p className="mb-0">Address: {walletData.address}</p>
          </div>

          <div className="card-body">
            <div className="mb-4">
              <h3>Persona Category</h3>
              <h4>{walletData.persona.category || 'Unknown'}</h4>
              <div className="my-3">
                {Array.isArray(walletData.persona.tags) && walletData.persona.tags.map((tag, index) => (
                  <span key={index} className="badge bg-secondary me-2 mb-2">
                    {tag}
                  </span>
                ))}
              </div>
              <p>{walletData.persona.bio || 'No bio available'}</p>
            </div>

            <div className="mb-4">
              <div className="row">
                <div className="col-md-6">
                  <h3>Risk Assessment</h3>
                  <div className="d-flex align-items-center">
                    <h4 className={`text-${walletData.persona.riskScore < 30 ? 'success' : walletData.persona.riskScore < 70 ? 'warning' : 'danger'}`}>
                      {walletData.persona.riskScore || 0}/100
                    </h4>
                    <span className="ms-3">
                      ({walletData.persona.riskScore < 30 ? 'Low' : walletData.persona.riskScore < 70 ? 'Medium' : 'High'} Risk)
                    </span>
                  </div>
                </div>
                <div className="col-md-6">
                  <h3>Activity Level</h3>
                  <h4>{walletData.persona.activityLevel || 'Unknown'}</h4>
                </div>
              </div>
            </div>

            <div>
              <h3>Personalized Recommendations</h3>
              {Array.isArray(walletData.persona.recommendations) && walletData.persona.recommendations.length > 0 ? (
                <ul className="list-group">
                  {walletData.persona.recommendations.map((rec, index) => (
                    <li key={index} className="list-group-item">
                      {rec}
                    </li>
                  ))}
                </ul>
              ) : (
                <p>No recommendations available</p>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
